        public static async Task<string> PostFileWithStringContent(Feature data, string url)
        {
            HttpResponseMessage response = null;
            string f = "";
            try
            {
                using (var formData = new MultipartFormDataContent())
                {
                    formData.Headers.Add("Proxy", "Authorization: Basic MWYxMWU3ZDExNTZlMzFhNWQxN2QxOTlhNjk3ODJjYmQ6c3QyU1ppeVZhOVpMbUZNdg==");
                    formData.Add(new StringContent(data.properties.Name), "Name");
                    formData.Add(new StringContent(data.properties.description), "Description");
                    formData.Add(new StringContent(data.properties.description), "SmallDescription");
                    formData.Add(new StringContent(data.geometry.coordinates[1].ToString("G", CultureInfo.InvariantCulture)), "Coordinates.Lat");
                    formData.Add(new StringContent(data.geometry.coordinates[0].ToString("G", CultureInfo.InvariantCulture)), "Coordinates.Lng");
                    formData.Add(new StringContent(data.properties.Name.ToLower().Contains("музей") ? "0" : data.properties.Name.ToLower().Contains("памятник") ? "1" : GetType(data.properties.TYPE)), "Type");
                    response = await (new HttpClient()).PostAsync(url, formData);
                    f = await response.Content.ReadAsStringAsync();
                }
            }
            catch (Exception ex)
            {
                System.Diagnostics.Debug.WriteLine($"Upload failed {ex.Message}");
            }

            return await response.Content.ReadAsStringAsync();
        }

        private static string GetType(string type) =>
            type switch
            {
                "Гостиницы" => "6",
                "Достопримечательности" => "2",
                "Кафе, рестораны" => "7",
                "Сервис" => "2",
                "Сувениры, промыслы" => "2",
                _ => "2"
            };
